import React from 'react';
import Headers from './components/actions/view/Headers';
import Article from './components/actions/view/Article';
import Footer from './components/actions/view/Footer';
import './App.css';
import { Component } from 'react'; //importando component do react

class App extends Component {

  state = {
    Nome: [], //estado nome para nomes
  }

  async componentDidMount() {
    const response = await api.get('nome');//requisição pode ser vazio tbm

    this.setState({ Nome: response.data }); //recebe dadod response
  }

  render() {

    const { Nome } = this.state;

    return (
      <div>
        <h1>Resultado produto</h1>
        {Nome.map(Nome => (
          <li key={Nome.show.code}>
            <h2>
              <strong>Produto: </strong>
              {Nome.show.name}
            </h2>
            <p>
              {Nome.show.description}
            </p>

          </li>
        ))}
      </div>
    );
  };
};

function AppView(props) {
  return (
    <div>
      <Headers />
      <Article />
      <Footer />
    </div>
  );
};

export default AppView;
